sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the test date is before rightnow, return true, else return false
		if(testdate.before(rightnow)){
			return true;
		}
		else{
			return false;
		}
	},

	isDateToday: function(strDate){
		// Follow the pattern set by the isDatePast function, need variables for dates
		var today = new GlideDateTime().getDate();   // they used method getLocalDate()
		var testdate = new GlideDateTime(strDate);

		// oh wow, they use a very different method that what I came up with, I wonder
		// if mine will even work?

		// Now perform the test, returning true of the dates are equal, otherwise false
		if(testdate.equals(today)){
			return true;
		}
		else{
			return false;
		}
	},

    type: 'NeedItUtils'
};
